EXEC [dbo].pts_CheckProc 'pts_CIS_NewMember'
GO

--declare @Result varchar(1000) CIS pts_CIS_NewMember 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_CIS_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @Level int
SET @CompanyID = 15

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Level = [Level], @ReferralID = ReferralID FROM Member WHERE MemberID = @MemberID

-- Activate Status
IF @Level = 0
	UPDATE Member SET Status = 3 WHERE MemberID = @MemberID
ELSE
	UPDATE Member SET Status = 1 WHERE MemberID = @MemberID

IF @ReferralID > 0 
BEGIN
--	Update the upline group totals
	EXEC pts_CIS_SetTotals @ReferralID, 1, 0, @Result output
END

GO 
