EXEC [dbo].pts_CheckProc 'pts_CIS_PaymentPost'
GO
--declare @Result varchar(1000) EXEC pts_CIS_PaymentPost 23381, @Result output print @Result

CREATE PROCEDURE [dbo].pts_CIS_PaymentPost
   @PaymentID int ,
   @Result int OUTPUT
AS

SET NOCOUNT ON
SET @Result = 0

DECLARE @Today datetime, @Status int, @CommStatus int, @Count int, @PayDate datetime
SET @Today = dbo.wtfn_DateOnly( GETDATE() )
SET @Count = 0

SELECT @Status = [Status], @CommStatus = CommStatus, @PayDate = PayDate
FROM Payment WHERE PaymentID = @PaymentID

--	If the payment is Approved AND not a future payment
IF @Status = 3 AND @PayDate <= @Today
BEGIN
--	If the payment is not bonused yet - calc bonuses
	IF @CommStatus = 1 EXEC pts_Commission_Company_15a @PaymentID, @Count OUTPUT
END
SET @Result = @Count

GO 
