EXEC [dbo].pts_CheckProc 'pts_CloudZow_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_CloudZow_NewMember 572, @Result output print @Result

CREATE PROCEDURE [dbo].pts_CloudZow_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @SponsorID int, @Level int, @Title int, @BV money, @cnt int
SET @CompanyID = 5

-- *******************************************************************
--  New Affiliate/Customer Activation
--  - Assign affiliates to leadership teams
--  - Accumulate affiliate totals (highest title, personal volume, group volume)
--  - Process affiliate Advancements
-- *******************************************************************
--	-- Get member type (level = customer or affiliate), purchase price, member enroller and sponsor 
SELECT @Level = [Level], @BV = Price, @ReferralID = ReferralID, @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID

--	If this is a customer, update the enrolling affiliate's personal volume and check for possible advancement
IF @Level = 0 AND @ReferralID > 0
BEGIN
--	-- Add new sale to the affiliate's personal sales volume 
	UPDATE Member SET BV = BV + @BV, BV2 = BV2 + @BV WHERE MemberID = @ReferralID
--	-- Assign new Customer to 3 leadership teams (based on enroller)
	EXEC pts_Downline_Build_5 @CompanyID, @ReferralID, @MemberID
--	-- check for advancement for this affiliate
	EXEC pts_Commission_CalcAdvancement_5 @ReferralID, 0, @cnt OUTPUT
--	-- Get the referring affiliate's sponsor to accumulate group volume
	SELECT @SponsorID = SponsorID FROM Member WHERE MemberID = @ReferralID
END	

--	If this is an Affiliate
IF @Level = 1
BEGIN
--	Set Status to Active
	UPDATE Member SET Status = 1, MaxMembers = Title WHERE MemberID = @MemberID
	
--	-- Assign new Affiliate to 3 leadership teams (based on enroller)
	IF @ReferralID > 0
		EXEC pts_Downline_Build_5 @CompanyID, @ReferralID, @MemberID
		
--	-- Assign new Affiliate to the sponsor's team
	IF @SponsorID > 0
	BEGIN	
		SELECT @Title = Title FROM Member WHERE MemberID = @SponsorID
		UPDATE Member SET BusAccts = @Title WHERE MemberID = @MemberID
	END
END
--	 *************************************************************
--	 Accumulate group volume (based on sponsor)
--	 *************************************************************
SET @MemberID = @SponsorID

WHILE @MemberID > 0
BEGIN
	SET @SponsorID = -1
	SELECT @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID
--	-- TEST if we found the affiliate
	IF @SponsorID >= 0
	BEGIN
--		-- accumulate the new sale in the affiliate's group sales volume
		UPDATE Member SET QV = QV + @BV, QV2 = QV2 + @BV, BV4 = BV4 + 1 WHERE MemberID = @MemberID 

--		-- check for advancement for this affiliate
		EXEC pts_Commission_CalcAdvancement_5 @MemberID, 0, @cnt OUTPUT

		SET @MemberID = @SponsorID
	END
	ELSE SET @MemberID = 0
END

SET @Result = '1'

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              