EXEC [dbo].pts_CheckProc 'pts_CloudZow_Sale'
GO

--declare @Result varchar(1000) EXEC pts_CloudZow_Sale 1, 0, 0, 0, @Result output print @Result

CREATE PROCEDURE [dbo].pts_CloudZow_Sale
   @MemberID int ,
   @Amount int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @MemberID2 int, @ReferralID int, @SponsorID int, @Title int, @Level int, @Now datetime, @cnt int, @inc int
DECLARE @Total int, @MaxTitle int, @BadPayment int, @BV money, @QV money, @BV4 money 

SET @CompanyID = 5

-- *******************************************************************
-- Update change in Group Sales Volume
-- *******************************************************************
--	Amount is passed in as a positive or negative number
SET @BV = @Amount

--	Get member type (level = customer or affiliate), purchase price, member enroller and sponsor 
SELECT @Level = [Level], @ReferralID = ReferralID, @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID

--	If this is an affiliate, check if the adjusted personal volume caused a possible advancement
IF @Level = 1
BEGIN

--	check for advancement for this affiliate
	EXEC pts_Commission_CalcAdvancement_5 @MemberID, 0, @cnt OUTPUT
END	

--	If this is a customer, update the enrolling affiliate's personal volume and check for possible advancement
IF @Level = 0 AND @ReferralID > 0
BEGIN
--	update affiliate's personal sales volume 
	UPDATE Member SET BV = BV + @BV, BV2 = BV2 + @BV WHERE MemberID = @ReferralID
--	check for advancement for this affiliate
	EXEC pts_Commission_CalcAdvancement_5 @ReferralID, 0, @cnt OUTPUT
--	Get the referring affiliate's sponsor to adjust group volume
	SELECT @SponsorID = SponsorID FROM Member WHERE MemberID = @ReferralID
END	

--	 *************************************************************
--	 Adjust group volume (based on sponsor)
--	 *************************************************************
SET @MemberID = @SponsorID
WHILE @MemberID > 0
BEGIN
	SET @SponsorID = -1
	SELECT @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID
--	TEST if we found the affiliate
	IF @SponsorID >= 0
	BEGIN
--		-- adjust the affiliate's group sales volume
		UPDATE Member SET QV = QV + @BV, QV2 = QV2 + @BV WHERE MemberID = @MemberID 

--		-- check for advancement for this affiliate
		EXEC pts_Commission_CalcAdvancement_5 @MemberID, 0, @cnt OUTPUT

		SET @MemberID = @SponsorID
	END
	ELSE SET @MemberID = 0
END

SET @Result = '1'

GO

