EXEC [dbo].pts_CheckProc 'pts_CloudZow_Sponsor'
GO

--declare @Result varchar(1000) EXEC pts_CloudZow_Sponsor 1704, @Result output print @Result

CREATE PROCEDURE [dbo].pts_CloudZow_Sponsor
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
--preset 
SET @Result = '521'

DECLARE @SponsorID int, @Status int

-- Use ReferralID if no SponsorID (Customer)
SELECT @MemberID = ReferralID, @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID
IF @SponsorID > 0 SET @MemberID = @SponsorID

WHILE @MemberID > 0
BEGIN
	SET @SponsorID = -1
	SELECT @SponsorID = SponsorID, @Status = Status FROM Member WHERE MemberID = @MemberID
--	-- TEST if we found the affiliate
	IF @SponsorID >= 0
	BEGIN
		IF @Status = 1
		BEGIN
			SET @Result = CAST(@MemberID AS VARCHAR(10))
			SET @MemberID = 0
		END
		ELSE SET @MemberID = @SponsorID
	END
	ELSE SET @MemberID = 0
END

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              