EXEC [dbo].pts_CheckProc 'pts_CloudZow_Upgrade'
GO

--declare @Result varchar(1000) EXEC pts_CloudZow_Upgrade 565, @Result output print @Result

CREATE PROCEDURE [dbo].pts_CloudZow_Upgrade
   @MemberID int ,
   @Amount int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @Status int, @ReferralID int, @SponsorID int, @Price money, @BV money, @cnt int
SET @CompanyID = 5

-- *******************************************************************
-- Customer to Affiliate upgrade
--  - Assign affiliates to leadership teams
--  - Accumulate affiliate totals (highest title, personal volume, group volume)
--  - Process affiliate Advancements
-- *******************************************************************
	
--	Get member type (level = customer or affiliate), purchase price, member enroller and sponsor 
SELECT @Status = Status, @Price = Price, @ReferralID = ReferralID, @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID

-- 	@BV is the difference of new price and old price
SET @BV = @Price - @Amount

--	If this is an affiliate, check if the adjusted personal volume caused a possible advancement

--	update referring affiliate's personal and group volume 
UPDATE Member SET BV = BV - @Amount,BV2 = BV2 - @Amount, QV = QV + @Amount,QV2= QV2 + @Amount WHERE MemberID = @SponsorID

--	Assign new Affiliate to 3 leadership teams (based on enroller) if he is still on trial.
IF @Status = 2 
BEGIN
	EXEC pts_Downline_Build_5 @CompanyID, @ReferralID, @MemberID
	Update member set Status = 1 where memberID = @MemberID		
END

--	 *************************************************************
--	 Accumulate group volume (based on sponsor)
--	 *************************************************************
SET @MemberID = @SponsorID

WHILE @MemberID > 0
BEGIN
	SET @SponsorID = -1
	SELECT @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID
--	TEST if we found the affiliate
	IF @SponsorID >= 0
	BEGIN
--		-- accumulate the new sale in the affiliate's group sales volume, and downline count
		UPDATE Member SET QV = QV + @BV, QV2 = QV2 + @BV, BV4 = BV4 + 1 WHERE MemberID = @MemberID 

--		-- check for advancement for this affiliate
		EXEC pts_Commission_CalcAdvancement_5 @MemberID, 0, @cnt OUTPUT

		SET @MemberID = @SponsorID
	END
	ELSE SET @MemberID = 0
END

SET @Result = '1'

GO
