EXEC [dbo].pts_CheckProc 'pts_FreeLoyalty_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_FreeLoyalty_NewMember 10758, @Result output print @Result

CREATE PROCEDURE [dbo].pts_FreeLoyalty_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @SponsorID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int
SET @CompanyID = 18

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @ReferralID = ReferralID FROM Member WHERE MemberID = @MemberID

-- Activate Status
UPDATE Member SET Status = 1, MaxMembers = Title WHERE MemberID = @MemberID

IF @ReferralID > 0 
BEGIN
--	Update the upline group totals
	EXEC pts_FreeLoyalty_SetTotals @ReferralID, 1, 0, @Title, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_18 @ReferralID, 0, @cnt OUTPUT
END

GO 
