EXEC [dbo].pts_CheckProc 'pts_GCR_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_GCR_NewMember 13912, @Result output print @Result

CREATE PROCEDURE [dbo].pts_GCR_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @SponsorID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @Dup int, @NameFirst varchar(30), @NameLast varchar(30)
SET @CompanyID = 17
SET @Dup = 0

-- *******************************************************************
-- New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @SponsorID = SponsorID, @NameFirst = NameFirst, @NameLast = NameLast 
FROM Member WHERE MemberID = @MemberID

-- *******************************************************************
-- Check for Duplicate Records
-- *******************************************************************
SELECT @Dup = COUNT(*) FROM Member WHERE CompanyID = @CompanyID AND NameLast = @NameLast AND NameFirst = @NameFirst AND MemberID <> @MemberID
IF @Dup > 0
BEGIN
--	Flag this duplicate name account for manual review
	UPDATE Member SET NameLast = '**' + NameLast WHERE MemberID = @MemberID
END

--	Activate Status, highest title and matrix width
UPDATE Member SET Status = 2, MaxMembers = Title, Process = 2 WHERE MemberID = @MemberID

--	Place this new member in the expandable Matrix (width=0) (if not already assigned at enrollment)
--IF @SponsorID = 0 AND @Title > 1 
--BEGIN
--	SET @SponsorID = @ReferralID
--	SET @Level = 0 SET @Pos = 0 
--	EXEC pts_Member_PlaceSponsor -2, @SponsorID OUTPUT, @Level OUTPUT, @Pos OUTPUT
--	UPDATE Member SET SponsorID = @SponsorID WHERE MemberID = @MemberID
--END

IF @ReferralID > 0 
BEGIN
--		Assign new Affiliate to 3 coded teams (based on enroller)
-- THIS IS NOW DONE AFTER SUCCESSFUL PAYMENT
--	EXEC pts_Downline_Build_17 @CompanyID, @ReferralID, @MemberID
	
--		Update the upline group totals
	EXEC pts_GCR_SetTotals @ReferralID, 1, 0, @Title, @Result output
--	EXEC pts_GCR_SetTotals2 @SponsorID, 1, 0, @Result output

--		check if the new member's upline earned an advancement
--	EXEC pts_Commission_CalcAdvancement_17 @ReferralID, 0, @cnt OUTPUT
END

