EXEC [dbo].pts_CheckProc 'pts_GCR_Placement'
GO

-- EXEC pts_GCR_Placement 35402

CREATE PROCEDURE [dbo].pts_GCR_Placement
   @MemberID int 
AS

SET NOCOUNT ON

DECLARE @CompanyID int, @ReferralID int, @SponsorID int, @Title int, @cnt int, @Level int, @Pos int, @Result varchar(1000)

SET @CompanyID = 17

SELECT @ReferralID = ReferralID, @SponsorID = SponsorID, @Title = Title FROM Member WHERE MemberID = @MemberID

SELECT @cnt = COUNT(*) FROM Downline WHERE ChildID = @MemberID

IF @cnt = 0 
BEGIN
--	Assign new Affiliate to 3 coded teams (based on enroller)
	EXEC pts_Downline_Build_17 @CompanyID, @ReferralID, @MemberID
END

IF @SponsorID = 0 AND @Title > 1
BEGIN
--	Place in Matrix
	SET @SponsorID = @ReferralID
	SET @Level = 0 SET @Pos = 0 
	EXEC pts_Member_PlaceSponsor -2, @SponsorID OUTPUT, @Level OUTPUT, @Pos OUTPUT
	UPDATE Member SET SponsorID = @SponsorID WHERE MemberID = @MemberID
	EXEC pts_GCR_SetTotals2 @SponsorID, 1, 0, @Result output
END

GO 
