EXEC [dbo].pts_CheckProc 'pts_GCR_SetTotals2'
GO

--declare @Result varchar(1000) EXEC pts_GCR_SetTotals2 7166, 1, @Result output print @Result

CREATE PROCEDURE [dbo].pts_GCR_SetTotals2
   @MemberID int ,
   @Count int ,
   @Recalc int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @SponsorID int, @QV2 money, @Legs int, @cnt int, @Title int, @level int

IF @Recalc = 0 UPDATE Member SET BV2 = BV2 + 1 WHERE MemberID = @MemberID

SET @level = 1

WHILE @MemberID > 0
BEGIN
	SET @SponsorID = -1
--	Get the current Affiliate's sponsor, personal total, group total, title
	SELECT @SponsorID = SponsorID, @QV2 = QV2, @Legs = Process, @Title = Title FROM Member WHERE MemberID = @MemberID

--print 'Member: ' + cast( @memberid as varchar(10) ) + ' Count: ' + cast( @Count as varchar(10) )

--	TEST if we found the affiliate
	IF @SponsorID >= 0
	BEGIN
--		--Update the group total, and highest title for the current affiliate
		UPDATE Member SET QV2 = QV2 + @Count WHERE MemberID = @MemberID

		IF @Recalc > 0 AND @QV2 = 0 SET @Count = @Count + 1
		IF @Recalc = 0
		BEGIN
--			-- recalc the total 3rd level members this new member is on
			IF @level = 3
			BEGIN
				UPDATE Member SET QV3 = (
					SELECT COUNT(C.MemberID)
					FROM Member As A
					JOIN Member AS B ON A.MemberID = B.SponsorID
					JOIN Member AS C ON B.MemberID = C.SponsorID
					WHERE A.SponsorID = Member.MemberID AND C.Status >= 1 AND C.Status <= 4
				)
				WHERE MemberID = @MemberID
			END
			SET @level = @level + 1
		END 

--		-- check for new legs (2x3) for Diamond+ only (QV3 = total members on 3rd level)
--		IF @Title >= 6
--		BEGIN
--			SELECT @cnt = COUNT(*) FROM Member WHERE SponsorID = @MemberID AND QV3 >= 8
--			IF @cnt > 0 AND @cnt+2 > @Legs UPDATE Member SET Process = @cnt + 2 WHERE MemberID = @MemberID
--		END
		
		SET @MemberID = @SponsorID
	END
	ELSE SET @MemberID = 0
END

GO 
