EXEC [dbo].pts_CheckProc 'pts_GCR_SetTotals2'
GO

--declare @Result varchar(1000) EXEC pts_GCR_SetTotals2 23079, 1, 1, @Result output print @Result

CREATE PROCEDURE [dbo].pts_GCR_SetTotals2
   @MemberID int ,
   @Count int ,
   @Recalc int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @Temp TABLE(MemberID int)
DECLARE @SponsorID int, @QV2 money, @cnt int

IF @Recalc = 0 UPDATE Member SET BV2 = BV2 + 1 WHERE MemberID = @MemberID

WHILE @MemberID > 0
BEGIN
	SET @SponsorID = -1
--	Get the current Affiliate's sponsor, personal total, group total, title
	SELECT @SponsorID = SponsorID, @QV2 = QV2 FROM Member WHERE MemberID = @MemberID 

--print 'Member: ' + cast( @memberid as varchar(10) ) + ' Count: ' + cast( @Count as varchar(10) )
	
--	TEST if we found the affiliate
	IF @SponsorID >= 0
	BEGIN
--		CHECK if this sponsor has already been processed (Loop)
		SELECT @cnt = COUNT(*) FROM @Temp WHERE MemberID = @SponsorID
		IF @cnt > 0 
--			UPDATE Member SET SponsorID = 0 WHERE MemberID = @MemberID
			SET @SponsorID = -1
		ELSE
		BEGIN
			INSERT INTO @Temp (MemberID) VALUES (@SponsorID)

--			Update the group total, and highest title for the current affiliate
			UPDATE Member SET QV2 = QV2 + @Count WHERE MemberID = @MemberID

			IF @Recalc > 0 AND @QV2 = 0 SET @Count = @Count + 1
		END
		SET @MemberID = @SponsorID
	END
	ELSE SET @MemberID = 0
END

GO 
