EXEC [dbo].pts_CheckProc 'pts_GCR_Upgrade'
GO

--declare @Result varchar(1000) EXEC pts_GCR_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_GCR_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Status int, @Level int, @Options2 varchar(40), @SponsorID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @tmpReferralID int, @Today datetime
SET @CompanyID = 17
SET @Today = dbo.wtfn_DateOnly(GETDATE())

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @ReferralID = ReferralID, @SponsorID = SponsorID, @Title = Title FROM Member WHERE MemberID = @MemberID

--EXEC pts_GCR_Placement @MemberID

--Create new payment for free member
IF @Title = 1
BEGIN
--	Set status to active to create the payment
	UPDATE Member SET Status = 1 WHERE MemberID = @MemberID
	EXEC pts_GCR_Payments @Today, @Result OUTPUT
--	Set status back to trial until payment is made
	UPDATE Member SET Status = 2 WHERE MemberID = @MemberID
END

--IF @ReferralID > 0 
--BEGIN
----	check if the new member's upline earned an advancement
--	EXEC pts_Commission_CalcAdvancement_17 @ReferralID, 0, @cnt OUTPUT
--END

GO 
