EXEC [dbo].pts_CheckProc 'pts_GFTG_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_GFTG_NewMember 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_GFTG_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @SponsorID int, @cnt int, @Title int, @Level int, @Pos int, @Width int, @Legs int, @RefTitle int
DECLARE @Amount money, @BinarySaleID int
SET @CompanyID = 13

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID

-- Activate Status
IF @Level = 0
	UPDATE Member SET Status = 3, MaxMembers = Title WHERE MemberID = @MemberID
ELSE
BEGIN
	UPDATE Member SET Status = 1, MaxMembers = Title WHERE MemberID = @MemberID

--	--Place this new member in the matrix and coded teams
--	Default matrix width
	SET @Width = 3
	
--	Expand Legs if referrer is active Emerald+ and has completed 3x2 legs
	SET @RefTitle = 0
	SELECT @RefTitle = Title, @Legs = Process FROM Member WHERE MemberID = @ReferralID AND Status = 1
	IF @RefTitle >= 5 AND @Legs > 0 SET @Width = @Legs
	
--	--Place this new member in the expandable Matrix (width=0) (if not already assigned at enrollment)
	IF @SponsorID = 0
	BEGIN
		SET @SponsorID = @ReferralID
		SET @Level = 0 SET @Pos = 0 
		EXEC pts_Member_PlaceSponsor -3, @SponsorID OUTPUT, @Level OUTPUT, @Pos OUTPUT
		UPDATE Member SET SponsorID = @SponsorID WHERE MemberID = @MemberID
	END

--	Update the upline sponsor group totals, and completed leg counts
	EXEC pts_GFTG_SetSponsorTotals @SponsorID, 1, 0, @Result output

--	-- Assign new Affiliate to 6 coded teams (based on enroller)
	IF @ReferralID > 0 
		EXEC pts_Downline_Build_13 @CompanyID, @ReferralID, @MemberID
END

IF @ReferralID > 0 
BEGIN
--	Update the upline group totals
	EXEC pts_GFTG_SetTotals @ReferralID, 1, 0, @Title, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_13 @ReferralID, 0, @cnt OUTPUT
END

GO 
