EXEC [dbo].pts_CheckProc 'pts_GFTG_SetLegs'
GO

--declare @Legs int EXEC pts_GFTG_SetLegs 7967, @Legs output print @Legs

CREATE PROCEDURE [dbo].pts_GFTG_SetLegs
   @MemberID int,
   @Legs int OUTPUT
AS

SET NOCOUNT ON
SET @Legs = 0

-- *******************************************************************
-- Get the number sponsored legs that have at least 12 active downline 
-- and have at least 3 members on their 1st level
-- and have at least 9 members on their 2nd level
-- *******************************************************************

SELECT @Legs = COUNT(me.MemberID) 
FROM Member AS me 
WHERE me.SponsorID = @MemberID 
AND me.QV2 >= 12
AND 3 <= (
	SELECT COUNT(B.MemberID) 
	FROM Member As A
	JOIN Member AS B ON A.MemberID = B.SponsorID
	WHERE A.SponsorID = me.MemberID
)
AND 9 <= (
	SELECT COUNT(C.MemberID) 
	FROM Member As A
	JOIN Member AS B ON A.MemberID = B.SponsorID
	JOIN Member AS C ON B.MemberID = C.SponsorID
	WHERE A.SponsorID = @MemberID
)

UPDATE Member SET Process = @Legs WHERE MemberID = @MemberID AND Process != @Legs

GO 
