EXEC [dbo].pts_CheckProc 'pts_GFTG_Upgrade'
GO

--declare @Result varchar(1000) EXEC pts_GFTG_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_GFTG_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int
SET @CompanyID = 13

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @Sponsor3ID = Sponsor3ID, @Pos = Pos, @QV4 = QV4 FROM Member WHERE MemberID = @MemberID

-- check if the new member's upline earned an advancement
EXEC pts_Commission_CalcAdvancement_13 @ReferralID, 0, @cnt OUTPUT

GO 
