EXEC [dbo].pts_CheckProc 'pts_Legacy_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_Legacy_NewMember 10758, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Legacy_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @SponsorID int, @PayID int, @CommType int, @CardType int, @CardName varchar(50), @Email varchar(80)
SET @CompanyID = 14

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @SponsorID = SponsorID, @Sponsor3ID = Sponsor3ID, @Pos = Pos, @QV4 = QV4, @PayID = PayID, @Email = Email FROM Member WHERE MemberID = @MemberID

-- Activate Status
IF @Level = 0
	UPDATE Member SET Status = 3, MaxMembers = Title, Process = 4 WHERE MemberID = @MemberID
ELSE
BEGIN
	IF @Level = 3  
		UPDATE Member SET Status = 2, MaxMembers = Title, Process = 4 WHERE MemberID = @MemberID
	Else	
		UPDATE Member SET Status = 1, MaxMembers = Title, Process = 4 WHERE MemberID = @MemberID
END
	
--Double Check Payout Method Wallet ID	
SELECT @CommType = CommType, @CardType = CardType, @CardName = CardName FROM Billing WHERE BillingID = @PayID
IF @CommType = 4 AND @CardName = ''
BEGIN
	IF @CardType = 11 UPDATE Billing SET CardName = CAST(@MemberID as varchar(10)) WHERE BillingID = @PayID
	IF @CardType = 12 UPDATE Billing SET CardName = @Email WHERE BillingID = @PayID
END
	
--	Check to make sure this pre-assigned spot is still available
IF @Sponsor3ID > 0
BEGIN
	SELECT @cnt = COUNT(*) FROM Member WHERE Sponsor3ID = @Sponsor3ID AND Pos = @Pos AND Status >= 1 AND Status <= 4
	IF @cnt != 1 SET @Sponsor3ID = 0
END
IF @ReferralID > 0 AND @Sponsor3ID <= 0  
BEGIN
	SET @tmpReferralID = @ReferralID
	EXEC pts_Member_PlaceBinary3 @tmpReferralID OUTPUT, @Pos OUTPUT
	SET @Sponsor3ID = @tmpReferralID
	UPDATE Member SET Sponsor3ID = @Sponsor3ID, Pos = @Pos WHERE MemberID = @MemberID
END

--	-- Assign new Affiliate to 5 coded teams (based on enroller)
IF @ReferralID > 0  EXEC pts_Downline_Build_14 @CompanyID, @ReferralID, @MemberID
	
--	Update the upline binary totals
EXEC pts_Legacy_SetTotalsB @Sponsor3ID, 1, @Result output

IF @ReferralID > 0 
BEGIN
--	Matrix Placement
	IF @SponsorID = 0 AND @Title > 0
	BEGIN
	--	Place in Matrix
		SET @SponsorID = @ReferralID
		SET @Level = 0 SET @Pos = 0 
		EXEC pts_Member_PlaceSponsor -4, @SponsorID OUTPUT, @Level OUTPUT, @Pos OUTPUT
		UPDATE Member SET SponsorID = @SponsorID WHERE MemberID = @MemberID
		EXEC pts_Legacy_SetTotals2 @SponsorID, 1, 0, @Result output
	END

--	Update the upline group totals
	EXEC pts_Legacy_SetTotals @ReferralID, 1, 0, @Title, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_14 @ReferralID, 0, @cnt OUTPUT
END

IF @ReferralID <= 0
BEGIN
	SET @ReferralID = 10287 -- Top corporate position
	UPDATE Member SET ReferralID = @ReferralID WHERE MemberID = @MemberID
END


GO 
