EXEC [dbo].pts_CheckProc 'pts_Legacy_Upgrade'
GO

--declare @Result varchar(1000) EXEC pts_Legacy_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Legacy_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Status int, @Level int, @Options2 varchar(40), @SponsorID int, @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @tmpReferralID int, @Today datetime
SET @CompanyID = 14
SET @Today = dbo.wtfn_DateOnly(GETDATE())

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @Status = Status, @Level = [Level], @ReferralID = ReferralID, @SponsorID = SponsorID, @Sponsor3ID = Sponsor3ID FROM Member WHERE MemberID = @MemberID

IF @Level = 3
BEGIN
	UPDATE Member SET Status = 1, Level = 1, PaidDate = @Today WHERE MemberID = @MemberID

--	Matrix Placement
	IF @SponsorID = 0 AND @ReferralID > 0  
	BEGIN
	--	Place in Matrix
		SET @SponsorID = @ReferralID
		SET @Level = 0 SET @Pos = 0 
		EXEC pts_Member_PlaceSponsor -4, @SponsorID OUTPUT, @Level OUTPUT, @Pos OUTPUT
		UPDATE Member SET SponsorID = @SponsorID WHERE MemberID = @MemberID
		EXEC pts_Legacy_SetTotals2 @SponsorID, 1, 0, @Result output
	END

--	If they are not in the binary
	IF @Sponsor3ID = 0
	BEGIN
--		Place them in the Binary
		SET @tmpReferralID = @ReferralID
		EXEC pts_Member_PlaceBinary3 @tmpReferralID OUTPUT, @Pos OUTPUT
		SET @Sponsor3ID = @tmpReferralID
		UPDATE Member SET Sponsor3ID = @Sponsor3ID, Pos = @Pos WHERE MemberID = @MemberID

--		Assign new Affiliate to 5 coded teams (based on enroller)
		IF @ReferralID > 0  EXEC pts_Downline_Build_14 @CompanyID, @ReferralID, @MemberID
	
--		Update the upline binary totals
		EXEC pts_Legacy_SetTotalsB @Sponsor3ID, 1, @Result output
	END
END

IF @ReferralID > 0 
BEGIN
--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_14 @ReferralID, 0, @cnt OUTPUT
END

GO 
