EXEC [dbo].pts_CheckProc 'pts_Nexxus_GiftConsolidate'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_GiftConsolidate 504, 37702, 1, @Result output print @Result
--select * from Payment where paymentid = 164489 164531

CREATE PROCEDURE [dbo].pts_Nexxus_GiftConsolidate
   @Status int ,
   @MemberID int ,
   @Qty int ,
   @Result varchar(1000) OUTPUT
AS

-- *****************************
-- Consolidate $2 Certificates
-- Status 601: $10 (5)
-- Status 602: $25 (13)
-- Status 603: $50 (30) 
-- *****************************

SET NOCOUNT ON
SET @Result = '0'

DECLARE @cert int, @cnt int, @certs int, @ID int, @x int, @GiftDate datetime, @NewGiftDate datetime, @PaymentID int, @NewPaymentID int, @Notes varchar(100)
SET @cert = 0
IF @Status = 601
BEGIN
	SET @cert = 10
	SET @cnt = 5 * @Qty
END
IF @Status = 602
BEGIN
	SET @cert = 25
	SET @cnt = 13 * @Qty
END
IF @Status = 603
BEGIN
	SET @cert = 50
	SET @cnt = 30 * @Qty
END

IF @cert > 0
BEGIN	
--	Check if they have enough $2 certificates for the request
	SELECT @certs = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 2
	IF @cnt <= @certs
	BEGIN
--		Delete the $2 Certificates
		DECLARE Gift_cursor CURSOR LOCAL STATIC FOR 
		SELECT TOP (@cnt) GiftID, GiftDate, PaymentID
		FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 2 ORDER BY GiftID DESC
		OPEN Gift_cursor
		FETCH NEXT FROM Gift_cursor INTO @ID, @GiftDate, @PaymentID
		SET @x = 0
		WHILE @@FETCH_STATUS = 0
		BEGIN
			SET @x = @x + 1
--			Save the oldest GiftDate and PaymentID for the new consolidated certificates
			IF @x = 1
			BEGIN
				SET @NewGiftDate = @GiftDate
				SET @NewPaymentID = @PaymentID
			END	
			DELETE Gift WHERE GiftID = @ID
			FETCH NEXT FROM Gift_cursor INTO @ID, @GiftDate, @PaymentID
		END
		CLOSE Gift_cursor
		DEALLOCATE Gift_cursor

--		Create the new consolidated certificates
		SET @Notes = 'Consolidated ' + dbo.wtfn_DateOnlyStr( GETDATE())
		SET @x = 0
		WHILE @x < @Qty
		BEGIN
			SET @x = @x + 1
--			@GiftID,MemberID,PaymentID,Member2ID,GiftDate,ActiveDate,Amount,Purpose,Notes,UserID
			EXEC pts_Gift_Add @ID, @MemberID, @NewPaymentID, 0, @NewGiftDate, 0, @cert, '', @Notes, 1  
		END
	END	

	SET @Result = CAST(@Qty AS VARCHAR(5))
END

GO 
    
