EXEC [dbo].pts_CheckProc 'pts_Nexxus_GiftCount'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_GiftCount 37702, 2, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Nexxus_GiftCount
   @MemberID int ,
   @Type int ,
   @Result varchar(100) OUTPUT
AS

SET NOCOUNT ON
DECLARE @cnt int
DECLARE @Today datetime, @Retail money, @Total money, @RetailDate datetime, @RetailStr varchar(10)

IF @Type = 0
BEGIN
	SET @Result = '0'
	SELECT @cnt = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 2
	SET @Result = CAST(@cnt AS VARCHAR(10))
END
ELSE
BEGIN
	SET @Result = ''
	SELECT @cnt = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 10
	SET @Result = CAST(@cnt AS VARCHAR(10))
	SELECT @cnt = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 25
	SET @Result = @Result + ';' + CAST(@cnt AS VARCHAR(10))
	SELECT @cnt = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 50
	SET @Result = @Result + ';' + CAST(@cnt AS VARCHAR(10))
	IF @Type = 2
	BEGIN
		SELECT @cnt = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = 2
		SET @Result = @Result + ';' + CAST(@cnt AS VARCHAR(10))
		
		SET @Today = dbo.wtfn_DateOnly(GETDATE())
		SET @RetailDate = DATEADD(day,-31,@Today)
		SELECT @Total = ISNULL(SUM(Amount),0) FROM Gift WHERE MemberID = @MemberID AND GiftDate < @RetailDate
		SELECT @Retail = ISNULL(SUM(Amount),0) FROM Gift WHERE MemberID = @MemberID AND GiftDate < @RetailDate  AND Member2ID > 0
		IF @Total > 0
			SET @RetailStr = CAST( CAST( (@Retail / @Total) * 100 AS INT ) AS varchar(10))
		ELSE 
			SET @RetailStr = '100'	
		SET @Result = @Result + ';' + CAST(@Retail AS VARCHAR(15)) + ';' + CAST(@Total AS VARCHAR(15)) + ';' + @RetailStr
	END
END

GO 
    
