EXEC [dbo].pts_CheckProc 'pts_Nexxus_GiftSplit'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_GiftSplit 611, 37702, 2, @Result output print @Result
--declare @Result varchar(1000) EXEC pts_Nexxus_GiftCount 37702, 1, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Nexxus_GiftSplit
   @Status int ,
   @MemberID int ,
   @Qty int ,
   @Result varchar(1000) OUTPUT
AS

-- *****************************
-- Consolidate $2 Certificates
-- Status 611: $10 (5)
-- Status 612: $25 (13)
-- Status 613: $50 (30) 
-- *****************************

SET NOCOUNT ON
SET @Result = '0'

DECLARE @cert int, @cnt int, @certs int, @ID int, @x int, @z int, @GiftDate datetime, @NewGiftDate datetime, @PaymentID int, @NewPaymentID int, @Notes varchar(100)
SET @cert = 0
IF @Status = 611
BEGIN
	SET @cert = 10
	SET @cnt = 5
END
IF @Status = 612
BEGIN
	SET @cert = 25
	SET @cnt = 13
END
IF @Status = 613
BEGIN
	SET @cert = 50
	SET @cnt = 30
END

IF @cert > 0
BEGIN	
--	Check if they have enough specified certificates for the request
	SELECT @certs = COUNT(*) FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = @cert
	IF @Qty <= @certs
	BEGIN
--		Delete the sepcified Certificates
		DECLARE Gift_cursor CURSOR LOCAL STATIC FOR 
		SELECT TOP (@Qty) GiftID, GiftDate, PaymentID
		FROM Gift WHERE MemberID = @MemberID AND Member2ID = 0 AND Purpose = '' AND Amount = @cert ORDER BY GiftID DESC
		OPEN Gift_cursor
		FETCH NEXT FROM Gift_cursor INTO @ID, @GiftDate, @PaymentID
		WHILE @@FETCH_STATUS = 0
		BEGIN
--			Save the GiftDate and PaymentID for the new split certificates
			SET @NewGiftDate = @GiftDate
			SET @NewPaymentID = @PaymentID
			DELETE Gift WHERE GiftID = @ID
			
--			Create the new split $2 certificates
			SET @Notes = 'Split ' + dbo.wtfn_DateOnlyStr( GETDATE())
			SET @z = 0
			WHILE @z < @cnt
			BEGIN
				SET @z = @z + 1
--				@GiftID,MemberID,PaymentID,Member2ID,GiftDate,ActiveDate,Amount,Purpose,Notes,UserID
				EXEC pts_Gift_Add @ID, @MemberID, @NewPaymentID, 0, @NewGiftDate, 0, 2, '', @Notes, 1  
			END
			FETCH NEXT FROM Gift_cursor INTO @ID, @GiftDate, @PaymentID
		END
		CLOSE Gift_cursor
		DEALLOCATE Gift_cursor
	END	

	SET @Result = CAST(@cnt AS VARCHAR(5))
END

GO 
    
