EXEC [dbo].pts_CheckProc 'pts_Nexxus_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_NewMember 39657, @Result output  print @Result

CREATE PROCEDURE [dbo].pts_Nexxus_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @Dup int, @NameFirst varchar(30), @NameLast varchar(30)
SET @CompanyID = 21
SET @Dup = 0

-- *******************************************************************
-- New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @Sponsor3ID = Sponsor3ID, @Pos = Pos, @NameFirst = NameFirst, @NameLast = NameLast 
FROM Member WHERE MemberID = @MemberID

--Process New Customers
IF @Level = 0
BEGIN
	--	Activate Status
	UPDATE Member SET Status = 3 WHERE MemberID = @MemberID
END

--Process New Affiliates
IF @Level = 1
BEGIN
--	Check for Duplicate Records
	SELECT @Dup = COUNT(*) FROM Member WHERE CompanyID = @CompanyID AND NameLast = @NameLast AND NameFirst = @NameFirst AND MemberID <> @MemberID AND Status NOT IN (0,3)
	IF @Dup > 0
	BEGIN
	--	Flag this duplicate name account for manual review
		UPDATE Member SET NameLast = '**' + NameLast WHERE MemberID = @MemberID
	END

	--	Activate Status, pay title
	UPDATE Member SET Status = 1, Title2 = Title WHERE MemberID = @MemberID

--	Create /Assign Shopper Account
	EXEC pts_Nexxus_MemberShopper @MemberID

--	Check to make sure this pre-assigned spot is still available
	IF @Sponsor3ID > 0
	BEGIN
		SELECT @cnt = COUNT(*) FROM Member WHERE Sponsor3ID = @Sponsor3ID AND Pos = @Pos AND Status >= 1 AND Status <= 4
		IF @cnt != 1 SET @Sponsor3ID = 0
	END
	
--	Place in the binary downline	
	IF @Sponsor3ID <= 0
	BEGIN
		SET @tmpReferralID = @ReferralID
		EXEC pts_Member_PlaceBinary3 @tmpReferralID OUTPUT, @Pos OUTPUT
		SET @Sponsor3ID = @tmpReferralID
		UPDATE Member SET Sponsor3ID = @Sponsor3ID, Pos = @Pos WHERE MemberID = @MemberID
	END

--	Update the upline binary totals
	IF @Sponsor3ID > 0 EXEC pts_Nexxus_SetTotalBinary @Sponsor3ID, 1, @Result output
	
--	Update the upline enroller and upline totals
	IF @ReferralID > 0 EXEC pts_Nexxus_SetTotalCount @ReferralID, 1, 0, @Result output

END

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              