EXEC [dbo].pts_CheckProc 'pts_Nexxus_PaymentAd'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_PaymentAd 165612, @Result output print @Result
--select * from Payment where paymentid = 164489 164531

CREATE PROCEDURE [dbo].pts_Nexxus_PaymentAd
   @PaymentID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @Qty int, @cnt int, @Purpose nvarchar (10), @ID int, @OwnerType int, @OwnerID int, @Today datetime, @Retail money, @Amount money, @Reference varchar(30)

SET @Today = dbo.wtfn_DateOnly(GETDATE())

-- Check if this has already been processed
SELECT @cnt = COUNT(*) FROM BarterCredit WHERE Reference = CAST(@PaymentID AS VARCHAR(10))

IF @cnt = 0
BEGIN
	SELECT @OwnerType = OwnerType, @OwnerID = OwnerID, @Purpose = Purpose, @Retail = Retail FROM Payment WHERE PaymentID = @PaymentID
	
	If @Purpose = '106' SET @Amount = 12
	If @Purpose = '107' SET @Amount = 35
	If @Purpose = '108' SET @Amount = 100
	If @Purpose = '116' SET @Amount = 10
	If @Purpose = '117' SET @Amount = 30
	If @Purpose = '118' SET @Amount = 65

	SET @Qty = @Retail
	IF @Qty = 0 SET @Qty = 1
	SET @Amount = @Amount * @Qty
	SET @Reference = CAST(@PaymentID AS VARCHAR(10))	
	
   -- BarterCreditID,OwnerType,OwnerID,BarterAdID,CreditDate,Amount,Status,CreditType,StartDate,EndDate,Reference,Notes,UserID
	EXEC pts_BarterCredit_Add @ID, @OwnerType, @OwnerID, 0, @Today, @Amount, 1, 1, 0, 0, @Reference, '', 1
END

SET @Result = CAST(@cnt AS VARCHAR(5))

GO 
    
