EXEC [dbo].pts_CheckProc 'pts_Nexxus_PaymentGift'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_PaymentGift 165612, @Result output print @Result
--select * from Payment where paymentid = 164489 164531

CREATE PROCEDURE [dbo].pts_Nexxus_PaymentGift
   @PaymentID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @Qty int, @PV int,  @cnt int, @total int, @Purpose nvarchar (10), @ID int, @MemberID int, @Today datetime, @Retail money

SET @Today = dbo.wtfn_DateOnly(GETDATE())
SET @cnt = 0

-- Check if this has already been processed
SELECT @total = COUNT(*) FROM Gift WHERE PaymentID = @PaymentID

IF @total = 0
BEGIN
	SELECT @MemberID = OwnerID, @Purpose = Purpose, @Retail = Retail FROM Payment WHERE PaymentID = @PaymentID
	
	SET @Qty = 0
	If @Purpose = '100'
	BEGIN
--		Get Qty and PV from Retail Price (1.25 = 1 qty, 25 pv)
		SET @Qty = FLOOR(@Retail)
		SET @PV = (@Retail - @Qty) * 100
	END
	If @Purpose = '101' -- Beginner Package - 5 * $2
	BEGIN
		SET @Qty = 5
		SET @PV = 2
	END
	If @Purpose = '102' -- Basic Package - 13 * $2
	BEGIN
		SET @Qty = 13
		SET @PV = 2
	END
	If @Purpose = '103' -- Pro Package - 30 * $2
	BEGIN
		SET @Qty = 30
		SET @PV = 2
	END
	SET @cnt = 0
	WHILE @cnt < @Qty
	BEGIN
		SET @cnt = @cnt + 1
		--GiftID,MemberID,PaymentID,Member2ID,GiftDate,ActiveDate,Amount,Purpose,Notes,UserID
		EXEC pts_Gift_Add @ID, @MemberID, @PaymentID, 0, @Today, 0, @PV, '', '', 1 
	END
END

SET @Result = CAST(@cnt AS VARCHAR(5))

GO 
    
