CREATE PROCEDURE [dbo].pts_Nexxus_Placement
   @MemberID int 
AS

SET NOCOUNT ON

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @Sponsor3ID int, @Title int, @cnt int, @Level int, @Pos int, @Result varchar(1000)

SET @CompanyID = 21

SELECT @ReferralID = ReferralID, @Sponsor3ID = Sponsor3ID, @Title = Title FROM Member WHERE MemberID = @MemberID

IF @Sponsor3ID = 0 AND @Title > 1
BEGIN
	SET @tmpReferralID = @ReferralID
	EXEC pts_Member_PlaceBinary3 @tmpReferralID OUTPUT, @Pos OUTPUT
	SET @Sponsor3ID = @tmpReferralID
	UPDATE Member SET Sponsor3ID = @Sponsor3ID, Pos = @Pos WHERE MemberID = @MemberID
	EXEC pts_Nexxus_SetTotalBinary @Sponsor3ID, 1, @Result output
END

