EXEC [dbo].pts_CheckProc 'pts_Nexxus_SetTotalCount'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_SetTotalCount 7166, 1, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Nexxus_SetTotalCount
   @MemberID int ,
   @Count int ,
   @Recalc int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @ReferralID int, @QV money

IF @Recalc = 0 UPDATE Member SET BV = BV + 1 WHERE MemberID = @MemberID

WHILE @MemberID > 0
BEGIN
	SET @ReferralID = -1
--	Get the current Affiliate's sponsor, personal total, group total, title
	SELECT @ReferralID = ReferralID, @QV = QV FROM Member WHERE MemberID = @MemberID

--print 'Member: ' + cast( @memberid as varchar(10) ) + ' Count: ' + cast( @Count as varchar(10) )

--	TEST if we found the affiliate
	IF @ReferralID >= 0
	BEGIN
--		--Update the group total for the current affiliate
		UPDATE Member SET QV = QV + @Count WHERE MemberID = @MemberID

		IF @Recalc > 0 AND @QV = 0 SET @Count = @Count + 1

		SET @MemberID = @ReferralID
	END
	ELSE SET @MemberID = 0
END

GO 
