EXEC [dbo].pts_CheckProc 'pts_Nexxus_SetTotalSales'
GO

--declare @Result varchar(1000) EXEC pts_Nexxus_SetTotalSales 7166, 1, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Nexxus_SetTotalSales
   @MemberID int ,
   @Sales money ,
   @Recalc int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @ReferralID int, @QV2 money

IF @Recalc = 0 UPDATE Member SET BV2 = BV2 + @Sales WHERE MemberID = @MemberID

WHILE @MemberID > 0
BEGIN
	SET @ReferralID = -1
--	Get the current Affiliate's sponsor, personal total, group total, title
	SELECT @ReferralID = ReferralID, @QV2 = QV2 FROM Member WHERE MemberID = @MemberID

--print 'Member: ' + cast( @memberid as varchar(10) ) + ' Sales: ' + cast( @Sales as varchar(10) )

--	TEST if we found the affiliate
	IF @ReferralID >= 0
	BEGIN
--		--Update the group total for the current affiliate
		UPDATE Member SET QV2 = QV2 + @Sales WHERE MemberID = @MemberID

--???		IF @Recalc > 0 AND @QV2 = 0 SET @Sales = @Sales + @BV2

		SET @MemberID = @ReferralID
	END
	ELSE SET @MemberID = 0
END

GO 
