EXEC [dbo].pts_CheckProc 'pts_PB_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_PB_NewMember 9209, @Result output print @Result

CREATE PROCEDURE [dbo].pts_PB_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Level int, @Price money, @Options2 varchar(100), @Credit int
DECLARE @PaymentID int, @Total money, @Purpose varchar(100), @PayDate datetime, @PayType int, @tmpCount int, @Status int
SET @CompanyID = 11

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Price = InitPrice, @Options2 = Options2 FROM Member WHERE MemberID = @MemberID

-- ********************************************************************************************************
-- Process New Partner
IF @Level > 0
BEGIN
--	Activate Status
	UPDATE Member SET Status = 1 WHERE MemberID = @MemberID

--	Update the upline group totals
	EXEC pts_PB_SetTotals @ReferralID, 1, 0, @Title, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_11 @ReferralID, 0, @cnt OUTPUT

--	Create Glory Card Credit for Business Starter kit
	SET @Total = 0
	IF @Title = 3 SET @Total = 6
	IF @Title = 4 SET @Total = 10
	IF @Title = 5 SET @Total = 30
	SET @PayDate = dbo.wtfn_DateOnly(GETDATE())
	SET @Purpose = 'Glory Card Credit'
	SET @PayType = 91
	IF @Level = 1 SET @Status = 3 ELSE SET @Status = 0

	IF @Total > 0
	BEGIN
--		Create Payment Record - PaymentID OUTPUT,CompanyID,OwnerType,OwnerID,BillingID,@ProductID,@PaidID,PayDate,PaidDate, PayType,
--				Amount,Total,Credit,Retail,Commission,Description,Purpose,Status,Reference,Notes,CommStatus,CommDate,TokenType, TokenOwner,Token,UserID
		EXEC pts_Payment_Add @PaymentID OUTPUT, @CompanyID, 4, @MemberID, 0, 0, 0, @PayDate, 0, @PayType, 
				 @Total, @Total, 0, 0, 0, '', @Purpose, @Status, '', '', 1, 0, 0, 0, 0, 1
	END			     
END

-- ********************************************************************************************************
-- Process New Customer
IF @Level = 0
BEGIN
--	Activate Status
	UPDATE Member SET Status = 3 WHERE MemberID = @MemberID

--	If the customer paid anything
	IF @Price > 0
	BEGIN
--		check if the new member's upline earned an advancement
		EXEC pts_Commission_CalcAdvancement_11 @ReferralID, 0, @cnt OUTPUT
	END
END

GO 
