EXEC [dbo].pts_CheckProc 'pts_PB_Upgrade'
GO

--declare @Result varchar(1000) EXEC pts_PB_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_PB_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Level int
SET @CompanyID = 11

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID FROM Member WHERE MemberID = @MemberID

-- Update titles
UPDATE Member SET MaxMembers = Title WHERE MemberID = @MemberID

-- check if the new member's upline earned an advancement
EXEC pts_Commission_CalcAdvancement_11 @ReferralID, 0, @cnt OUTPUT

GO 
