EXEC [dbo].pts_CheckProc 'pts_PB_ValidPayment'
GO

-- EXEC pts_PB_ValidPayment 17849

CREATE PROCEDURE [dbo].pts_PB_ValidPayment
   @PaymentID int 
AS

SET NOCOUNT ON

DECLARE @Amount int, @Purpose varchar(100), @Purpose2 varchar(100)

SELECT @Amount = Amount, @Purpose = Purpose FROM Payment WHERE PaymentID = @PaymentID
SET @Purpose2 = @Purpose

-- If Junior Kit, remove if not enough money
IF CHARINDEX('101', @Purpose) > 0
BEGIN
	IF @Amount < 299.00
		SET @Purpose2 = REPLACE(@Purpose2, '101', '')
	Else	
		SET @Purpose2 = REPLACE(@Purpose2, '211', '')
END	

-- If Partner Kit, remove if not enough money
IF CHARINDEX('102', @Purpose) > 0
BEGIN
	IF @Amount < 499.00
		SET @Purpose2 = REPLACE(@Purpose2, '102', '')
	Else	
		SET @Purpose2 = REPLACE(@Purpose2, '211', '')
END	

-- If Senior Kit, remove if not enough money
IF CHARINDEX('103', @Purpose) > 0
BEGIN
	IF @Amount < 1500.00
		SET @Purpose2 = REPLACE(@Purpose2, '103', '')
	Else	
		SET @Purpose2 = REPLACE(@Purpose2, '211', '')
END	

-- If Single Glory Card, remove if not enough money, otherwise remove monthly charge
IF CHARINDEX('201', @Purpose) > 0
BEGIN
	IF @Amount < 249.00
		SET @Purpose2 = REPLACE(@Purpose2, '201', '')
	Else	
		SET @Purpose2 = REPLACE(@Purpose2, '211', '')
END	

-- If Family Glory Card, remove if not enough money, otherwise remove monthly charge
IF CHARINDEX('202', @Purpose) > 0
BEGIN
	IF @Amount < 399.00
		SET @Purpose2 = REPLACE(@Purpose2, '202', '')
	Else	
		SET @Purpose2 = REPLACE(@Purpose2, '212', '')
END	

-- If Family Glory Card, remove if not enough money, otherwise remove monthly charge
IF CHARINDEX('203', @Purpose) > 0
BEGIN
	IF @Amount < 949.00
		SET @Purpose2 = REPLACE(@Purpose2, '203', '')
	Else	
		SET @Purpose2 = REPLACE(@Purpose2, '213', '')
END	

IF @Purpose <> @Purpose2
BEGIN
	UPDATE Payment SET Purpose = @Purpose2 WHERE PaymentID = @PaymentID
END

GO 
