EXEC [dbo].pts_CheckProc 'pts_PeopleEdge_PaymentPost'
GO

--declare @Result varchar(1000) EXEC pts_PeopleEdge_PaymentPost 17849, @Result output print @Result

CREATE PROCEDURE [dbo].pts_PeopleEdge_PaymentPost
   @PaymentID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @Status int, @CommStatus int, @Count int
SET @Count = 0

SELECT @Status = [Status], @CommStatus = CommStatus FROM Payment WHERE PaymentID = @PaymentID

-- If the payment is Approved and not bonused yet - calc bonuses
IF @Status = 3 AND @CommStatus = 1 EXEC pts_Commission_Company_16a @PaymentID, @Count OUTPUT

SET @Result = CAST(@Count AS VARCHAR(10))

GO 
