EXEC [dbo].pts_CheckProc 'pts_Pinnacle_DeclineCancel'
GO

--declare @Result int EXEC pts_Pinnacle_DeclineCancel 9, '7/25/13', @Result output print @Result

CREATE PROCEDURE [dbo].pts_Pinnacle_DeclineCancel
   @CompanyID int ,
   @EnrollDate datetime ,
   @Result int OUTPUT
AS

SET NOCOUNT ON

DECLARE @Now datetime
SET @Now = GETDATE()
--preset 
SET @Result = 0

-- Cancel Suspended Members with Declined Direct Payments
UPDATE me SET Status = 6, EndDate = @Now 
--SELECT *
FROM Payment AS pa
JOIN Member AS me ON pa.OwnerType = 4 AND pa.OwnerID = me.MemberID
WHERE me.CompanyID = @CompanyID AND me.Status = 4 AND me.Billing = 3
AND  pa.Status = 4 AND pa.PayDate < @EnrollDate

-- Cancel Suspended Members with Declined SalesOrder Payments
UPDATE me SET Status = 6, EndDate = @Now 
--SELECT *
FROM Payment AS pa
JOIN SalesOrder AS so ON pa.OwnerType = 52 AND pa.OwnerID = so.SalesOrderID
JOIN Member AS me ON so.MemberID = me.MemberID
WHERE me.CompanyID = @CompanyID AND me.Status = 4 AND me.Billing = 3
AND  pa.Status = 4 AND pa.PayDate < @EnrollDate


SELECT @Result = COUNT(*) FROM Member WHERE Status = 6 AND EndDate = @Now

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              