EXEC [dbo].pts_CheckProc 'pts_Pinnacle_DeclineSubmit'
GO

--declare @Result int EXEC pts_Pinnacle_DeclineSubmit 7, '12/25/13', @Result output print @Result

CREATE PROCEDURE [dbo].pts_Pinnacle_DeclineSubmit
   @CompanyID int ,
   @EnrollDate datetime ,
   @Result int OUTPUT
AS

SET NOCOUNT ON

DECLARE @Now datetime
SET @Now = GETDATE()
--preset 
SET @Result = 0

-- ReSubmit Decline Direct payments for active members (1-4)
UPDATE pa SET Status = 1, Reference = 'Resubmit' 
--SELECT *
FROM Payment AS pa
JOIN Member AS me ON pa.OwnerType = 4 AND pa.OwnerID = me.MemberID
WHERE me.CompanyID = @CompanyID AND me.Status >= 1 AND me.Status <= 4 AND me.Billing = 3
AND  pa.Status = 4 AND pa.PayDate < @EnrollDate

-- ReSubmit Decline SalesOrder payments for active members (1-4)
UPDATE pa SET Status = 1, Reference = 'Resubmit' 
--SELECT *
FROM Payment AS pa
JOIN SalesOrder AS so ON pa.OwnerType = 52 AND pa.OwnerID = so.SalesOrderID
JOIN Member AS me ON so.MemberID = me.MemberID
WHERE me.CompanyID = @CompanyID AND me.Status >= 1 AND me.Status <= 4 AND me.Billing = 3
AND  pa.Status = 4 AND pa.PayDate < @EnrollDate


SELECT @Result = COUNT(*) FROM Payment WHERE CompanyID = @CompanyID AND Status = 1 AND Reference = 'Resubmit'

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              