EXEC [dbo].pts_CheckProc 'pts_Pinnacle_Orphans'
GO

--declare @Result int EXEC pts_Pinnacle_Orphans 14565, 0, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Pinnacle_Orphans
   @MemberID int ,
   @Line int ,
   @Result int OUTPUT
AS

SET NOCOUNT ON

DECLARE @tmpMemberID int, @SponsorID int, @Status int, @cnt int, @cnt2 int
SET @cnt = 0

-- All Orphans
IF @Line = 0
BEGIN
	EXEC pts_Pinnacle_Orphans @MemberID, 1, @cnt2 OUTPUT
	SET @cnt = @cnt + @cnt2
	EXEC pts_Pinnacle_Orphans @MemberID, 2, @cnt2 OUTPUT
	SET @cnt = @cnt + @cnt2
END

-- Enroller Orphans
IF @Line = 1
BEGIN
	--save current member
	SET @tmpMemberID = @MemberID
	--Find Active Upline ReferralID
	SET @SponsorID = -1
	WHILE @SponsorID = -1
	BEGIN
		SELECT @SponsorID = ReferralID FROM Member WHERE MemberID = @MemberID
		--If we found the sponsor check their status
		IF @SponsorID > 0
		BEGIN
			SELECT @Status = Status FROM Member WHERE MemberID = @SponsorID
			--If the sponsor is cancelled look for the next upline sponsor
			IF @Status >= 6
			BEGIN
				 SET @MemberID = @SponsorID
				 SET @SponsorID = -1
			END 
		END 
	END
	SET @MemberID = @tmpMemberID
	SELECT @cnt = COUNT(*) FROM Member WHERE ReferralID = @MemberID AND Status < 6
	UPDATE Member SET ReferralID = @SponsorID, MentorID = @SponsorID WHERE ReferralID = @MemberID AND Status < 6
END

-- Matrix Orphans
IF @Line = 2
BEGIN
	--save current member
	SET @tmpMemberID = @MemberID
	--Find Active Upline ReferralID
	SET @SponsorID = -1
	WHILE @SponsorID = -1
	BEGIN
		SELECT @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID
		--If we found the sponsor check their status
		IF @SponsorID > 0
		BEGIN
			SELECT @Status = Status FROM Member WHERE MemberID = @SponsorID
			--If the sponsor is cancelled look for the next upline sponsor
			IF @Status >= 6
			BEGIN
				 SET @MemberID = @SponsorID
				 SET @SponsorID = -1
			END 
		END 
	END
	SET @MemberID = @tmpMemberID
	SELECT @cnt = COUNT(*) FROM Member WHERE SponsorID = @MemberID AND Status < 6
	UPDATE Member SET SponsorID = @SponsorID WHERE SponsorID = @MemberID AND Status < 6
END

SET @Result = @cnt

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              