EXEC [dbo].pts_CheckProc 'pts_Pinnacle_Referrer'
GO

--declare @Result int EXEC pts_Pinnacle_Referrer 4005, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Pinnacle_Referrer
   @MemberID int ,
   @Result int OUTPUT
AS

SET NOCOUNT ON
--preset 
SET @Result = '0'

DECLARE @ReferralID int, @Status int

SELECT @MemberID = ReferralID FROM Member WHERE MemberID = @MemberID

WHILE @MemberID > 0
BEGIN
	SET @ReferralID = -1
	SELECT @ReferralID = ReferralID, @Status = Status FROM Member WHERE MemberID = @MemberID
	IF @ReferralID >= 0
	BEGIN
		IF @Status >= 1 AND @Status <= 4
		BEGIN
			SET @Result = @MemberID
			SET @MemberID = 0
		END
		ELSE SET @MemberID = @ReferralID
	END
	ELSE SET @MemberID = 0
END

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              