EXEC [dbo].pts_CheckProc 'pts_Pinnacle_ValidSponsor3'
GO

--declare @Result int EXEC pts_Pinnacle_ValidSponsor3 9054, 8976, @Result output print @Result
--select * from Member where ReferralID = Sponsor3ID and referralid > 0

CREATE PROCEDURE [dbo].pts_Pinnacle_ValidSponsor3
   @Sponsor3ID int ,
   @ReferralID int ,
   @Result int OUTPUT
AS

SET NOCOUNT ON
--preset 
SET @Result = 0

DECLARE @MemberID int, @Status int

SET @MemberID = @Sponsor3ID

WHILE @MemberID > 0
BEGIN
	SET @Sponsor3ID = -1
	SELECT @Sponsor3ID = Sponsor3ID, @Status = Status FROM Member WHERE MemberID = @MemberID
	IF @Sponsor3ID >= 0
	BEGIN
		IF @ReferralID = @MemberID AND @Status >= 1 AND @Status <= 4
		BEGIN
			SET @Result = @MemberID
			SET @MemberID = 0
		END
		ELSE SET @MemberID = @Sponsor3ID
	END
	ELSE SET @MemberID = 0
END

GO                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              