EXEC [dbo].pts_CheckProc 'pts_SIMS_OrderStatus'
GO

--declare @Result varchar(1000) EXEC pts_SIMS_OrderStatus 7166, 1, @Result output print @Result

CREATE PROCEDURE [dbo].pts_SIMS_OrderStatus
   @Status int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @OldStatus int
SET @CompanyID = 7
SET @OldStatus = @Status - 1

--UPDATE si SET Status = @Status
SELECT *
FROM SalesItem AS si
JOIN SalesOrder AS so ON si.SalesOrderID = so.SalesOrderID
WHERE so.CompanyID = @CompanyID AND @Status = @OldStatus

UPDATE SalesOrder SET Status = @Status WHERE CompanyID = @CompanyID AND @Status = @OldStatus

select * from SalesOrder where salesorderid = 13563
select * from salesitem where salesorderid = 13563


GO 
