EXEC [dbo].pts_CheckProc 'pts_SIMS_PaymentPost'
GO

--declare @Result varchar(1000) EXEC pts_SIMS_PaymentPost 17849, @Result output print @Result

CREATE PROCEDURE [dbo].pts_SIMS_PaymentPost
   @PaymentID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @OwnerType int, @OwnerID int, @Status int, @CommStatus int, @Count int
SET @Count = 0

SELECT @OwnerType = OwnerType, @Status = [Status], @CommStatus = CommStatus FROM Payment WHERE PaymentID = @PaymentID

-- If this payment belongs to a member - convert it to a sales order
IF @OwnerType = 4 EXEC pts_SIMS_ConvertPayment @PaymentID, @Result output

-- If the payment is Approved and not bonused yet - calc bonuses
IF @Status = 3 AND @CommStatus = 1 EXEC pts_Commission_Company_9a @PaymentID, @Count OUTPUT

SET @Result = CAST(@Count AS VARCHAR(10))

GO 
