EXEC [dbo].pts_CheckProc 'pts_SilverHeart_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_SilverHeart_NewMember 10758, @Result output print @Result

CREATE PROCEDURE [dbo].pts_SilverHeart_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @Title int, @cnt int
SET @CompanyID = 20

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @ReferralID = ReferralID FROM Member WHERE MemberID = @MemberID

-- Activate Status
UPDATE Member SET Status = 1, MaxMembers = Title WHERE MemberID = @MemberID
	
IF @ReferralID > 0 
BEGIN
--	Assign new Affiliate to 3 coded teams (based on enroller)
	EXEC pts_Downline_Build_20 @CompanyID, @ReferralID, @MemberID
	
--	Update the upline group totals
	EXEC pts_SilverHeart_SetTotals @ReferralID, 1, 0, @Title, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_20 @ReferralID, 0, @cnt OUTPUT
END

GO 
