EXEC [dbo].pts_CheckProc 'pts_SilverHeart_Upgrade'
GO

--declare @Result varchar(1000) EXEC pts_SilverHeart_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_SilverHeart_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Status int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @tmpReferralID int, @Today datetime
SET @CompanyID = 20
SET @Today = dbo.wtfn_DateOnly(GETDATE())

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @Status = Status, @Level = [Level], @ReferralID = ReferralID, @Sponsor3ID = Sponsor3ID FROM Member WHERE MemberID = @MemberID

IF @Level = 3
BEGIN
	UPDATE Member SET Status = 1, Level = 1, PaidDate = @Today WHERE MemberID = @MemberID
END

IF @ReferralID > 0 
BEGIN
--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_20 @ReferralID, 0, @cnt OUTPUT
END

GO 
