EXEC [dbo].pts_CheckProc 'pts_Silver_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_Silver_NewMember 9319, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Silver_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @Level int, @Title int, @cnt int
SET @CompanyID = 10

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Level = [Level], @ReferralID = ReferralID, @Title = Title FROM Member WHERE MemberID = @MemberID

-- Activate Status
IF @Level = 0
	UPDATE Member SET Status = 3 WHERE MemberID = @MemberID
ELSE
	UPDATE Member SET Status = 1 WHERE MemberID = @MemberID

-- Assign new Affiliate to 5 coded teams (based on enroller)
IF @ReferralID > 0 EXEC pts_Downline_Build_10 @CompanyID, @ReferralID, @MemberID
	
--	Update the upline group totals
EXEC pts_Silver_SetTotals @ReferralID, 1, 0, @Title, @Result output

-- check if the new member's upline earned an advancement
--EXEC pts_Commission_CalcAdvancement_10 @ReferralID, 0, @cnt OUTPUT

-- Create $200 Cash Payment to be paid later
IF @Level = 1
BEGIN
	DECLARE @PaymentID int, @Total money, @Purpose varchar(100), @PayDate datetime, @PayType int, @tmpCount int 
	SET @Total = 200
	SET @Purpose = '102'
	SET @PayDate = dbo.wtfn_DateOnly(GETDATE())
	SET @PayType = 7
--	Create Payment Record - PaymentID OUTPUT,CompanyID,OwnerType,OwnerID,BillingID,@ProductID,@PaidID,PayDate,PaidDate, PayType,
--				Amount,Total,Credit,Retail,Commission,Description,Purpose,Status,Reference,Notes,CommStatus,CommDate,TokenType, TokenOwner,Token,UserID
	EXEC pts_Payment_Add @PaymentID OUTPUT, @CompanyID, 4, @MemberID, 0, 0, 0, @PayDate, 0, @PayType, 
			     @Total, @Total, 0, 0, 0, '', @Purpose, 1, '', '', 1, 0, 0, 0, 0, 1

--	Do Payment post processing
	EXEC pts_Silver_PaymentPost @PaymentID, @tmpCount OUTPUT
END

GO 
