--declare @Result varchar(1000) EXEC pts_Silver_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_Silver_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Level int
SET @CompanyID = 10

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID FROM Member WHERE MemberID = @MemberID

-- Update titles
UPDATE Member SET MaxMembers = Title WHERE MemberID = @MemberID

-- Assign new Affiliate to 5 coded teams (based on enroller)
IF @ReferralID > 0 EXEC pts_Downline_Build_10 @CompanyID, @ReferralID, @MemberID

-- check if the new member's upline earned an advancement
EXEC pts_Commission_CalcAdvancement_10 @ReferralID, 0, @cnt OUTPUT

GO 
