EXEC [dbo].pts_CheckProc 'pts_Silver_ValidPayment'
GO

-- EXEC pts_Silver_ValidPayment 17849

CREATE PROCEDURE [dbo].pts_Silver_ValidPayment
   @PaymentID int 
AS

SET NOCOUNT ON

DECLARE @Amount int, @Purpose varchar(100), @Purpose2 varchar(100)

SELECT @Amount = Amount, @Purpose = Purpose FROM Payment WHERE PaymentID = @PaymentID
SET @Purpose2 = @Purpose

-- If Diamond Pak, remove if not enough money
IF CHARINDEX('102', @Purpose) > 0
BEGIN
	IF @Amount < 200.00 SET @Purpose2 = REPLACE(@Purpose2, '102', '')
END	

IF @Purpose <> @Purpose2
BEGIN
	UPDATE Payment SET Purpose = @Purpose2 WHERE PaymentID = @PaymentID
END

GO 
