EXEC [dbo].pts_CheckProc 'pts_TellAll_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_TellAll_NewMember 10758, @Result output print @Result

CREATE PROCEDURE [dbo].pts_TellAll_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @SponsorID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int
SET @CompanyID = 19

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @SponsorID = SponsorID FROM Member WHERE MemberID = @MemberID

-- Activate Status, highest title and matrix width
UPDATE Member SET Status = 1, MaxMembers = Title, Process = 2 WHERE MemberID = @MemberID

-- Place this new member in the expandable Matrix (width=0) (if not already assigned at enrollment)
IF @Level = 1 AND @SponsorID = 0
BEGIN
	SET @SponsorID = @ReferralID
	SET @Level = 0 SET @Pos = 0 
	EXEC pts_Member_PlaceSponsor -2, @SponsorID OUTPUT, @Level OUTPUT, @Pos OUTPUT
	UPDATE Member SET SponsorID = @SponsorID WHERE MemberID = @MemberID
END

IF @ReferralID > 0 
BEGIN
--	Update the upline group totals
	EXEC pts_TellAll_SetTotals @ReferralID, 1, 0, @Title, @Result output
	EXEC pts_TellAll_SetTotals2 @SponsorID, 1, 0, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_19 @ReferralID, 0, @cnt OUTPUT
END

GO 
