CREATE PROCEDURE [dbo].pts_TellAll_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Status int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int, @tmpReferralID int, @Today datetime
SET @CompanyID = 19
SET @Today = dbo.wtfn_DateOnly(GETDATE())

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @ReferralID = ReferralID FROM Member WHERE MemberID = @MemberID

IF @ReferralID > 0 
BEGIN
--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_19 @ReferralID, 0, @cnt OUTPUT
END

GO 
