EXEC [dbo].pts_CheckProc 'pts_ZaZZed_NewMember'
GO

--declare @Result varchar(1000) EXEC pts_ZaZZed_NewMember 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_ZaZZed_NewMember
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @tmpReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int
SET @CompanyID = 9

-- *******************************************************************
--  New Member Activation
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @Sponsor3ID = Sponsor3ID, @Pos = Pos, @QV4 = QV4 FROM Member WHERE MemberID = @MemberID

-- Activate Status
IF @Level = 0
	UPDATE Member SET Status = 3, MaxMembers = Title WHERE MemberID = @MemberID
ELSE
	UPDATE Member SET Status = 1, MaxMembers = Title WHERE MemberID = @MemberID

--If this new member is on the Binary Plan
IF CHARINDEX( 'B', @Options2 ) > 0
BEGIN
--	Check to make sure this pre-assigned spot is still available
	IF @Sponsor3ID > 0
	BEGIN
		SELECT @cnt = COUNT(*) FROM Member WHERE Sponsor3ID = @Sponsor3ID AND Pos = @Pos AND Status >= 1 AND Status <= 4
		IF @cnt != 1 SET @Sponsor3ID = 0
	END
	IF @Sponsor3ID <= 0
	BEGIN
		SET @tmpReferralID = @ReferralID
		EXEC pts_Member_PlaceBinary3 @tmpReferralID OUTPUT, @Pos OUTPUT
		UPDATE Member SET Sponsor3ID = @tmpReferralID, Pos = @Pos WHERE MemberID = @MemberID
	END

--	-- Assign new Affiliate to 5 coded teams (based on enroller)
	IF @ReferralID > 0 
		EXEC pts_Downline_Build_9 @CompanyID, @ReferralID, @MemberID
	
--	Check that the BinarySale got processed after we assigned the Sponsor3ID	
--	IF @QV4 = 0
--	BEGIN
--		SET @BinarySaleID = 0
--		SELECT @BinarySaleID = BinarySaleID, @Amount = Amount FROM BinarySale WHERE MemberID = @MemberID 
--		IF @BinarySaleID > 0
--		BEGIN
----			-- Add the Binary Sales Volume to the upline 
--			EXEC pts_BinarySale_AddCustom @MemberID, 1, @Amount
----			-- Check for Binary Bonuses
--			EXEC pts_Commission_Company_9b @MemberID, 0, '', @cnt OUTPUT 
--		END	
--	END
	
--	Update the upline binary totals
	EXEC pts_ZaZZed_SetTotalsB @ReferralID, 1, @Result output
END
ELSE
BEGIN
--	Code this new member to an upline member
	EXEC pts_ZaZZed_Placement @MemberID, @Result output
END

IF @ReferralID > 0 
BEGIN
--	Update the upline group totals
	EXEC pts_ZaZZed_SetTotals @ReferralID, 1, 0, @Title, @Result output

--	check if the new member's upline earned an advancement
	EXEC pts_Commission_CalcAdvancement_9 @ReferralID, 0, @cnt OUTPUT
END

GO 
