EXEC [dbo].pts_CheckProc 'pts_ZaZZed_PaymentPost'
GO
--select * from payment where paymentid = 21937
--select * from SalesOrder where salesorderid = 15631
--declare @Result varchar(1000) EXEC pts_ZaZZed_PaymentPost 23381, @Result output print @Result
--SELECT OwnerType, OwnerID, Status FROM Payment WHERE PaymentID = 19698

CREATE PROCEDURE [dbo].pts_ZaZZed_PaymentPost
   @PaymentID int ,
   @Result int OUTPUT
AS

SET NOCOUNT ON
SET @Result = 0

DECLARE @OwnerType int, @OwnerID int, @Status int, @CommStatus int, @Count int, @MemberID int, @SalesOrderID int, @Options2 varchar(40)
DECLARE @Sponsor3ID int, @Today datetime, @PayDate datetime, @Notes varchar(500), @ReferralID int
SET @Today = dbo.wtfn_DateOnly( GETDATE() )
SET @Count = 0

SELECT @OwnerType = OwnerType, @OwnerID = OwnerID, @Status = [Status], @CommStatus = CommStatus, @PayDate = PayDate, @Notes = Notes 
FROM Payment WHERE PaymentID = @PaymentID

-- Process Submitted, Pending, Approved and Declined Payments
IF @Status >= 1 AND @Status <= 4 
BEGIN
--	If this payment belongs to a member - convert it to a sales order
	IF @OwnerType = 4
	BEGIN
		EXEC pts_ZaZZed_ValidPayment @PaymentID
		EXEC pts_ZaZZed_ConvertPayment @PaymentID, @Result output
		SET @MemberID = @OwnerID
		SET @SalesOrderID = CAST(@Result AS int)
	END
--	If this payment belongs to a sales order - get the member of the sales order
	IF @OwnerType = 52 
	BEGIN
		SET @SalesOrderID = @OwnerID
		SELECT @MemberID = MemberID FROM SalesOrder WHERE SalesOrderID = @SalesOrderID
	END

--	If the payment is Approved AND not a future payment
	IF @Status = 3 AND @PayDate <= @Today
	BEGIN
--		Check for Binary Plan Processing AND the payment hasn't already been binary processed (Notes = B*...)
		SELECT @Options2 = Options2, @ReferralID = ReferralID, @Status = Status FROM Member WHERE MemberID = @MemberID

--		-- Check if this is a free retail customer (Status = 3), if so get the referrer
		IF @Status = 3 SELECT @MemberID = MemberID, @Options2 = Options2 FROM Member WHERE MemberID = @ReferralID

		IF CHARINDEX( 'B', @Options2 ) > 0 AND CHARINDEX( 'B*', @Notes ) = 0
		BEGIN
			EXEC pts_ZaZZed_PaymentBinary @MemberID, @SalesOrderID
		END
--		If the payment is not bonused yet - calc bonuses
		IF @CommStatus = 1 EXEC pts_Commission_Company_9a @PaymentID, @Count OUTPUT
	END
	SET @Result = @Count
END

-- Process Cancelled and Returned Payments
IF @Status = 5 OR @Status = 6 
BEGIN
--	Only process Sales Order Payments
	IF @OwnerType = 52
	BEGIN
		SET @Result = 1
		SET @SalesOrderID = @OwnerID
		SELECT @MemberID = MemberID FROM SalesOrder WHERE SalesOrderID = @SalesOrderID

--		Check for Binary Plan Processing
		SELECT @Options2 = Options2, @ReferralID = ReferralID, @Status = Status FROM Member WHERE MemberID = @MemberID

--		-- Check if this is a free retail customer (Status = 3), if so get the referrer
		IF @Status = 3 SELECT @MemberID = MemberID, @Options2 = Options2 FROM Member WHERE MemberID = @ReferralID

		IF CHARINDEX( 'B', @Options2 ) > 0
		BEGIN
			SET @Result = 2
--			Check if we have already deducted this amount from the Binary Sales
			SELECT @Count = COUNT(*) FROM BinarySale WHERE RefID = @SalesOrderID AND SaleType = 3
			IF @Count = 0 
			BEGIN
				EXEC pts_ZaZZed_PaymentBinaryDeduct @MemberID, @SalesOrderID, 3
				SET @Result = 3
			END
		END
	END
END

GO 
