EXEC [dbo].pts_CheckProc 'pts_ZaZZed_SetTotalsB'
GO

--declare @Result varchar(1000) EXEC pts_ZaZZed_SetTotalsB 7948, 0, @Result output print @Result

CREATE PROCEDURE [dbo].pts_ZaZZed_SetTotalsB
   @MemberID int ,
   @Count int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @Sponsor3ID int, @BV3 money, @BV4 money

WHILE @MemberID > 0
BEGIN
	SET @Sponsor3ID = -1
--	Get the current Affiliate's sponsor, personal total, group total, title
	SELECT @Sponsor3ID = Sponsor3ID, @BV3 = BV3, @BV4 = BV4 FROM Member WHERE MemberID = @MemberID

--print 'Member: ' + cast( @memberid as varchar(10) ) + ' Count: ' + cast( @Count as varchar(10) )

--	TEST if we found the affiliate
	IF @Sponsor3ID >= 0
	BEGIN
--		--Update the group total, and highest title for the current affiliate
		UPDATE Member SET BV3=1, BV4 = BV4 + @Count WHERE MemberID = @MemberID

--		-- If BV3 = 0 it's the first time we counted this member
		IF @BV3 = 0 SET @Count = @Count + @BV4
		SET @MemberID = @Sponsor3ID
	END
	ELSE SET @MemberID = 0
END

GO 
