GO

--declare @Result varchar(1000) EXEC pts_ZaZZed_Upgrade 7166, @Result output print @Result

CREATE PROCEDURE [dbo].pts_ZaZZed_Upgrade
   @MemberID int ,
   @Result varchar(1000) OUTPUT
AS

SET NOCOUNT ON
SET @Result = '0'

DECLARE @CompanyID int, @ReferralID int, @cnt int, @Title int, @Level int, @Options2 varchar(40), @Sponsor3ID int, @Pos int, @QV4 money
DECLARE @Amount money, @BinarySaleID int
SET @CompanyID = 9

-- *******************************************************************
--  New Member Upgrade
-- *******************************************************************
-- Get new member's status and level
SELECT @Title = Title, @Level = [Level], @ReferralID = ReferralID, @Options2 = Options2, @Sponsor3ID = Sponsor3ID, @Pos = Pos, @QV4 = QV4 FROM Member WHERE MemberID = @MemberID

-- Update titles
UPDATE Member SET MaxMembers = Title WHERE MemberID = @MemberID

--If this member upgraded to the Binary Plan
IF CHARINDEX( 'B', @Options2 ) > 0
BEGIN
	IF @Sponsor3ID = 0
	BEGIN
--		-- Assign new Affiliate to 5 coded teams (based on enroller)
		IF @ReferralID > 0 EXEC pts_Downline_Build_9 @CompanyID, @ReferralID, @MemberID

		EXEC pts_Member_PlaceBinary3 @ReferralID OUTPUT, @Pos OUTPUT
		UPDATE Member SET Sponsor3ID = @ReferralID, Pos = @Pos WHERE MemberID = @MemberID

		EXEC pts_ZaZZed_SetTotalsB @ReferralID, 1, @Result output
	END
END

-- check if the new member's upline earned an advancement
EXEC pts_Commission_CalcAdvancement_9 @ReferralID, 0, @cnt OUTPUT

GO 
