EXEC [dbo].pts_CheckProc 'pts_ZaZZed_ValidPayment'
GO

-- EXEC pts_ZaZZed_ValidPayment 17849

CREATE PROCEDURE [dbo].pts_ZaZZed_ValidPayment
   @PaymentID int 
AS

SET NOCOUNT ON

DECLARE @Amount int, @Purpose varchar(100), @Purpose2 varchar(100)

SELECT @Amount = Amount, @Purpose = Purpose FROM Payment WHERE PaymentID = @PaymentID
SET @Purpose2 = @Purpose

-- If Diamond Pak, remove if not enough money
IF CHARINDEX('115', @Purpose) > 0
BEGIN
	IF @Amount < 199.95 SET @Purpose2 = REPLACE(@Purpose2, '115', '')
END	

-- If Wealth Builder, remove if not enough money
IF CHARINDEX('201', @Purpose) > 0
BEGIN
	IF @Amount < 99.95 SET @Purpose2 = REPLACE(@Purpose2, '201', '')
END	

IF @Purpose <> @Purpose2
BEGIN
	UPDATE Payment SET Purpose = @Purpose2 WHERE PaymentID = @PaymentID
END

GO 
