var cnt = 0, checked = 0;	
for (i = 0; i < Prospect.elements.length; i++) {
	if (getElementById(i).type == "checkbox") { 
		if (cnt < total) { 
			if (getElementById(i).checked != chk ) { 
				getElementById(i).checked = chk; 
				cnt++;
			}	
		}	
		if (getElementById(i).checked )  
			checked++;
	}	
}             
document.getElementById('Checked').value = checked;

var checked = 0;	
for (i = 0; i < Prospect.elements.length; i++) {
	if (getElementById(i).type == "checkbox") { 
		if (getElementById(i).checked )  
			checked++;
	}	
}             
document.getElementById('Checked').value = checked;

